import React from 'react';

function ImageCard({ src, alt, title, description }) {
  return (
    <div className="flex flex-col items-center md:flex-row md:items-start gap-4 p-4 max-w-md mx-auto bg-white rounded-lg shadow-md">
      <img
        src={src}
        alt={alt}
        className="w-20 h-20 rounded-lg object-cover transition-transform transform hover:scale-105"
      />
      <div className="text-center md:text-left transition-transform transform hover:scale-105">
        <h3 className="text-lg font-medium text-gray-900">{title}</h3>
        <p className="mt-0.5 text-gray-700">{description}</p>
      </div>
    </div>
  );
}

// Ejemplo de uso:
export default function App() {
  const cardData = {
    src: "https://images.unsplash.com/photo-1633332755192-727a05c4013d?q=80&w=2680&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    alt: "Example Image",
    title: "Title goes here",
    description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Voluptates voluptas distinctio nesciunt quas non animi.",
  };

  return (
    <div className="p-4">
      <ImageCard
        src={cardData.src}
        alt={cardData.alt}
        title={cardData.title}
        description={cardData.description}
      />
    </div>
  );
}
